import datetime
from dateutil.relativedelta import relativedelta


class QryClass:
    def __init__(self, dat, genPar):
        self.v_cmpcod = '' if not genPar['cmpcod'] else genPar['cmpcod']  # Company code
        self.v_fltcaridr = '' if not genPar['fltcaridr'] else str(genPar['fltcaridr'])  # Flight Carrier ID
        self.v_syscurcod = 'USD' if not genPar['sys_curcod'] else str(genPar['sys_curcod'])  # System currency code
        # self.v_density_factor = 1 if not genPar['density_factor'] else genPar['density_factor']  # Density factor

        if not dat:
            dat = datetime.datetime.now()
        self.currentDate = dat
        self.delta_4W = relativedelta(days=-28)
        self.delta_1Y = relativedelta(years=-1)
        self.cur_date = self.currentDate.strftime('%Y-%m-%d')
        self.his_dat_4W = self.currentDate + self.delta_4W
        self.his_dat_4W = self.his_dat_4W.strftime('%Y-%m-%d')
        self.his_dat_1Y = self.currentDate + self.delta_1Y
        self.his_dat_1Y = self.his_dat_1Y.strftime('%Y-%m-%d')
        self.createQryDict()  # Create queries based on dat

    def createQryDict(self):
        # print("set_exec_date.cur_dat:" + self.cur_date)
        # print("set_exec_date.his_dat_4W:" + self.his_dat_4W)
        self.query_FRP = {
            "qry_segrto_seglst": "SELECT DISTINCT CMPCOD,to_date('"
                                 + self.cur_date + "','YYYY-MM-DD') EXEDAT,FLTNUM, SEGORG,SEGDST,0 SUMCHGWGTSEG,0 SUMCHGWGTFLT,0 RTO,NULL DATPER,CURRENT_TIMESTAMP CRMLSTUPDTIM,MAX(fltrou) FLTROU FROM (SELECT CMPCOD,fltcarcod||fltnum fltnum,fltdat,SEGORG,SEGDST,(SELECT fltrou FROM crmfltoprmst WHERE fltnum=a.fltcarcod||a.fltnum AND fltdat=a.fltdat AND cmpcod=a.cmpcod ) fltrou FROM crmfltcapclsrul a WHERE fltcaridr="
                                 + self.v_fltcaridr + " AND a.SEGORG<>a.SEGDST AND fltdat BETWEEN to_date('"
                                 + self.his_dat_4W + "','YYYY-MM-DD') and to_date('"
                                 + self.cur_date + "','YYYY-MM-DD') UNION ALL SELECT CMPCOD,fltnum,fltdat,SEGORG,SEGDST,(SELECT fltrou FROM crmfltoprmst WHERE fltnum=b.fltnum AND fltdat  =b.fltdat AND cmpcod=b.cmpcod) fltrou FROM crmfltcapsegdcprul_prdcls b WHERE rultyp='BID' AND b.segorg<>b.segdst AND fltdat BETWEEN to_date('"
                                 + self.his_dat_4W + "','YYYY-MM-DD') AND to_date('"
                                 + self.cur_date + "','YYYY-MM-DD') UNION ALL  SELECT CMPCOD,fltnum,fltdat,ORGCOD SEGORG,ORGCOD SEGDST,(SELECT fltrou  FROM crmfltoprmst WHERE fltnum=c.fltnum AND fltdat=c.fltdat AND cmpcod=c.cmpcod) fltrou FROM crmcapfltalt c WHERE altsta<>'D' AND c.ORGCOD<>c.DSTCOD AND fltdat BETWEEN to_date('"
                                 + self.his_dat_4W + "','YYYY-MM-DD') AND to_date('"
                                 + self.cur_date + "','YYYY-MM-DD')) A where segorg<>segdst GROUP BY cmpcod,FLTNUM,SEGORG,SEGDST",
            "qry_segrto_hisvernum": "SELECT COALESCE(MAX(vernum),0)+1 vernum FROM CRMFLTEXPREVFCTSEGRTOHIS",
            "qry_bkg_1Y": "select bkgdcp.cmpcod,bkgdcp.fltnum,bkgdcp.fltdat,bkgdcp.segorg,bkgdcp.segdst,awborg,awbdst,(select ctycod from shrarpmst where cmpcod=bkgdcp.cmpcod and arpcod=bkgdcp.segorg) segorgcty, (select ctycod from shrarpmst where cmpcod=bkgdcp.cmpcod and arpcod=bkgdcp.segdst)  segdstcty, (select ctycod from shrarpmst where cmpcod=bkgdcp.cmpcod and arpcod=bkgdcp.awborg) awborgcty, (select ctycod from shrarpmst where cmpcod=bkgdcp.cmpcod and arpcod=bkgdcp.awbdst)  awbdstcty,chgwgt,scccod,prdnam,altidr,bkgdcp.flttyp,grsrevusdshp,grsrevusd,acrtypcod,fltawbwgt,fltratusd,fltopr.fltrou fltrou FROM crmcapbkgfltdcp bkgdcp,crmfltoprmst fltopr WHERE bkgdcp.fltdat BETWEEN to_date('"
                          + self.his_dat_1Y + "','YYYY-MM-DD') AND to_date('"
                          + self.cur_date + "','YYYY-MM-DD') AND bkgdcp.dcp=-1 AND bkgdcp.flttyp <> 'T' AND bkgdcp.bkgsta <> 'X' AND bkgdcp.cmpcod=fltopr.cmpcod AND bkgdcp.fltnum  =fltopr.fltnum AND bkgdcp.fltdat  =fltopr.fltdat",
            "qry_awm": "SELECT parval awm FROM shrsyspar WHERE parcod = 'cra.proration.awmfactorvalue' AND cmpcod='"
                       + self.v_cmpcod + "'",
            "qry_density_factor": "SELECT cast(parval as double precision) as densityfactor FROM shrsyspar WHERE parcod = 'capacity.allotment.weightFor1CBMVolume' AND cmpcod='"
                                  + self.v_cmpcod + "'",
            "qry_dow_start": " select case when A.parval='SUN' then 1 when A.parval='MON' then 2 when A.parval='TUE' then 3 when A.parval='WED' then 4 when A.parval='THU' then 5 when A.parval='FRI' then 6 when A.parval='SAT' then 7 else 1 end dowstr from (SELECT parval FROM shrsyspar WHERE parcod LIKE 'system.defaults.startday' and cmpcod='"
                             + self.v_cmpcod + "') A",
            "qry_alt_gblcus_netrat": "WITH tbl_altout AS (SELECT a.*,(SELECT STRING_AGG(DTL.GRPNAM,',' ORDER BY DTL.GRPENT) AS GRPNAM FROM SHRGENMSTGRP MST LEFT OUTER JOIN SHRGENMSTGRPDTL DTL ON MST.CMPCOD  = DTL.CMPCOD AND MST.GRPTYP =DTL.GRPTYP AND MST.GRPNAM =DTL.GRPNAM WHERE MST.CMPCOD =a.cmpcod AND MST.GRPTYP ='AGTGRP' AND a.fltdat BETWEEN dtl.vldfrmdat AND dtl.vldtoodat AND dtl.grpent =a.gblcuscod ) agtgrp, (SELECT STRING_AGG(catelm.ELMNAM, ',' ORDER BY catelm.CATCOD) FROM shrcatmst catmst LEFT OUTER JOIN shrcatelm catelm ON catmst.CATCOD =catelm.catcod AND catmst.CMPCOD=catelm.cmpcod AND cattyp ='P' AND catmst.catcod=a.catcod AND catmst.cmpcod=a.cmpcod ) prdnamlst, (SELECT STRING_AGG(catelm.ELMNAM, ',' ORDER BY catelm.CATCOD) FROM shrcatmst catmst LEFT OUTER JOIN shrcatelm catelm ON catmst.CATCOD =catelm.catcod AND catmst.CMPCOD=catelm.cmpcod AND cattyp ='H' AND catmst.catcod=a.catcod AND catmst.cmpcod=a.cmpcod ) hdlcod, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='ARPGRP' AND grpent =awborg AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awborgarpgrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='ARPGRP' AND grpent =awbdst AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awbdstarpgrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='CTYGRP' AND grpent =awborgcty AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awborgctygrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='CTYGRP' AND grpent =awbdstcty AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awbdstctygrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='CNTGRP' AND grpent =awborgcty AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awborgcntgrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='CNTGRP' AND grpent =awbdstcty AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awbdstcntgrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='REGGRP' AND grpent =awborgreg AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awborgreggrp, (SELECT grpnam FROM shrgenmstgrpdtl WHERE grpcatcod='TARIFF' AND grptyp ='REGGRP' AND grpent =awbdstreg AND cmpcod =a.cmpcod AND a.fltdat BETWEEN vldfrmdat AND vldtoodat ) awbdstreggrp FROM CRMFLTEXPREVFCT a WHERE rectyp  ='ALTGBLCUS' AND COALESCE(awborg,'-') <>'-' AND COALESCE(awbdst,'-') <>'-' AND exedat  =to_date('"
                                     + self.cur_date + "','YYYY-MM-DD')  AND cmpcod='"
                                     + self.v_cmpcod + "' ), tbl_TRFDTA AS (SELECT * FROM CRMTRFRATDTA b WHERE b.cmpcod ='"
                                     + self.v_cmpcod + "' AND b.PARAGTGRP  IS NULL AND b.PARARLCOD  IS NULL AND b.PARCAPTYP  IS NULL AND b.PARCARCOD  IS NULL AND b.PARCGNCOD  IS NULL AND b.PARCHDCOD  IS NULL AND b.PARCHDCODGRP IS NULL AND b.PARCMY IS NULL AND b.PARCMYCOD  IS NULL AND b.PARCMYCODGRP IS NULL AND b.PARCMYGRP  IS NULL AND b.PARCUS IS NULL AND b.PARCUSGRP  IS NULL AND b.PARDENGRP  IS NULL AND b.PARDOWGRP IS NULL AND b.PARFLTTYP  IS NULL AND b.PARGSTBASIS  IS NULL AND b.PARHDLCOD  IS NULL AND b.PARHDLCODGRP IS NULL AND b.PARIATCMYCOD IS NULL AND b.PARIBLOBL  IS NULL AND b.PARPRDCODGRP IS NULL AND b.PARROUTE IS NULL AND b.PARRTDFLTTYP IS NULL AND b.PARSHPCOD  IS NULL AND b.PARSHPCODGRP IS NULL AND b.PARSTATE IS NULL AND b.PARSTNGRP  IS NULL AND b.PARTRPMOD  IS NULL AND b.PARUNQNOT  IS NULL AND b.PARUNQREFFIVE  IS NULL AND b.PARUNQREFFOUR  IS NULL AND b.PARUNQREFONE IS NULL AND b.PARUNQREFTHREE IS NULL AND b.PARUNQREFTWO IS NOT NULL ) SELECT * FROM (SELECT CMPCOD , FLTNUM , FLTDAT , ALTIDR , STNCOD , EXEDAT, RATBSS, RATLINIDR, SERNUM, TRFNAM, TRFSERNUM, RECPRTVAL, RATUSD, MAX(RATUSD) OVER (PARTITION BY CMPCOD,FLTNUM,FLTDAT,ALTIDR,STNCOD,EXEDAT) AS MAXRATUSD FROM (SELECT CMPCOD , FLTNUM , FLTDAT , ALTIDR , STNCOD , EXEDAT, RATBSS, RATLINIDR, SERNUM, TRFNAM, TRFSERNUM, RECPRTVAL, WGTRAT RATLCL, curcod, ROUND(COALESCE("
                                     + "FUN_CONVCURR_NOROUNDING(cmpcod::varchar,curcod::varchar,'"
                                     + self.v_syscurcod + "'::varchar,'F'::varchar,FLTDAT::date,WGTRAT::float),0 )::numeric,6) RATUSD, CURRENT_TIMESTAMP crmlstupdtim FROM (SELECT altout.CMPCOD , altout.FLTNUM , altout.FLTDAT , altout.ALTIDR , altout.STNCOD , altout.EXEDAT, TRFDTA.ratbss, TRFDTA.RATLINIDR, TRFDTA.SERNUM, TRFDTA.TRFNAM, TRFDTA.TRFSERNUM, COALESCE(TRFDTA.RECPRTVAL,0) RECPRTVAL, MAX( COALESCE(TRFDTA.RECPRTVAL,0)) OVER (PARTITION BY altout.CMPCOD,altout.FLTNUM,altout.FLTDAT,altout.ALTIDR,altout.STNCOD,altout.EXEDAT) AS MAXRECPRTVAL,TRFDTA.wgtrat, TRFDTA.curcod FROM tbl_TRFDTA TRFDTA, tbl_altout altout WHERE altout.rectyp ='ALTGBLCUS' AND altout.fltdat BETWEEN TRFDTA.strdat AND COALESCE(TRFDTA.enddat,to_date('"
                                     + self.cur_date + "','YYYY-MM-DD')+100) AND COALESCE(TRFDTA.orgcod,'-')= CASE WHEN TRFDTA.orglvl='ARP' THEN altout.awborg WHEN TRFDTA.orglvl='ARPGRP' THEN altout.awborgarpgrp WHEN TRFDTA.orglvl='CTY' THEN altout.awborgcty WHEN TRFDTA.orglvl='CTYGRP' THEN altout.awborgctygrp WHEN TRFDTA.orglvl='CNT' THEN altout.awborgcnt WHEN TRFDTA.orglvl='CNTGRP' THEN altout.awborgcntgrp WHEN TRFDTA.orglvl='REG' THEN altout.awborgreg WHEN TRFDTA.orglvl='REGGRP' THEN altout.awborgreggrp WHEN TRFDTA.orglvl IS NULL THEN COALESCE(TRFDTA.orgcod,'-') END AND COALESCE(TRFDTA.dstcod,'-')= CASE WHEN TRFDTA.dstlvl='ARP' THEN altout.awbdst WHEN TRFDTA.dstlvl='ARPGRP' THEN altout.awbdstarpgrp WHEN TRFDTA.dstlvl='CTY' THEN altout.awbdstcty WHEN TRFDTA.dstlvl='CTYGRP' THEN altout.awbdstctygrp WHEN TRFDTA.dstlvl='CNT' THEN altout.awbdstcnt WHEN TRFDTA.dstlvl='CNTGRP' THEN altout.awbdstcntgrp WHEN TRFDTA.dstlvl='REG' THEN altout.awbdstreg WHEN TRFDTA.dstlvl='REGGRP' THEN altout.awbdstreggrp WHEN TRFDTA.dstlvl IS NULL THEN COALESCE(TRFDTA.dstcod,'-') END AND ( ( TRFDTA.PARUNQREFTWO  IS NOT NULL AND TRFDTA.PARUNQREFTWOINCEXCFLG='N' AND altout.altidr =TRFDTA.PARUNQREFTWO ) OR (TRFDTA.PARUNQREFTWO  IS NOT NULL AND TRFDTA.PARUNQREFTWOINCEXCFLG='Y' AND altout.altidr  <>TRFDTA.PARUNQREFTWO ) OR (TRFDTA.PARUNQREFTWO  IS NULL) ) AND ( ( TRFDTA.PARDOW  IS NOT NULL AND TRFDTA.PARDOWINCEXCFLG  ='N' AND TO_CHAR(altout.fltdat,'D')  =TRFDTA.PARDOW ) OR (TRFDTA.PARDOW  IS NOT NULL AND TRFDTA.PARDOWINCEXCFLG  ='Y' AND TO_CHAR(altout.fltdat,'D') <>TRFDTA.PARDOW ) OR (TRFDTA.PARDOW  IS NULL) ) AND ( ( TRFDTA.PARAGT  IS NOT NULL AND TRFDTA.PARAGTINCEXCFLG  ='N' AND altout.gblcuscod  =TRFDTA.PARAGT ) OR (TRFDTA.PARAGT  IS NOT NULL AND TRFDTA.PARAGTINCEXCFLG  ='Y' AND altout.gblcuscod <>TRFDTA.PARAGT ) OR (TRFDTA.PARAGT  IS NULL) ) AND ( ( TRFDTA.PARAGTGRP IS NOT NULL AND TRFDTA.PARAGTGRPINCEXCFLG ='N' AND (',' ||altout.AGTGRP ||',' ) LIKE ('%,' ||TRFDTA.PARAGTGRP ||',%') ) OR (TRFDTA.PARAGTGRP IS NOT NULL AND TRFDTA.PARAGTGRPINCEXCFLG ='Y' AND (',' ||altout.AGTGRP ||',' NOT LIKE ('%,' ||TRFDTA.PARAGTGRP ||',%') ) ) OR (TRFDTA.PARAGTGRP IS NULL) ) AND ( ( TRFDTA.PARSTN  IS NOT NULL AND TRFDTA.PARSTNINCEXCFLG  ='N' AND altout.stncod =TRFDTA.PARSTN ) OR (TRFDTA.PARSTN  IS NOT NULL AND TRFDTA.PARSTNINCEXCFLG  ='Y' AND altout.altidr  <>TRFDTA.PARSTN ) OR (TRFDTA.PARSTN  IS NULL) ) AND ( ( TRFDTA.PARPRDCOD IS NOT NULL AND TRFDTA.PARPRDCODINCEXCFLG ='N' AND (',' ||altout.prdnamlst ||',' ) LIKE ('%,' ||TRFDTA.PARPRDCOD ||',%') ) OR (TRFDTA.PARPRDCOD IS NOT NULL AND TRFDTA.PARPRDCODINCEXCFLG ='Y' AND (',' ||altout.PRDNAMLST ||',' NOT LIKE ('%,' ||TRFDTA.PARPRDCOD ||',%') ) ) OR (TRFDTA.PARPRDCOD  IS NULL) ) AND ( ( TRFDTA.PARFLTCOD  IS NOT NULL AND TRFDTA.PARFLTCODINCEXCFLG='N' AND altout.fltnum  =split_part(trfdta.parfltcod,'-',1) ||case when split_part(trfdta.parfltcod,'-',2)='' then '' else lpad(split_part(trfdta.parfltcod,'-',2),4,'0')  end ) OR (TRFDTA.PARFLTCOD  IS NOT NULL AND TRFDTA.PARFLTCODINCEXCFLG='Y' AND altout.fltnum <>split_part(trfdta.parfltcod,'-',1) ||case when split_part(trfdta.parfltcod,'-',2)='' then '' else lpad(split_part(trfdta.parfltcod,'-',2),4,'0')  end) OR (TRFDTA.PARFLTCOD  IS NULL) ) AND ( (TRFDTA.PARPAYTYP IS NOT NULL AND TRFDTA.PARPAYTYPINCEXCFLG='Y' AND 'CC' =TRFDTA.PARPAYTYP ) OR (TRFDTA.PARPAYTYP  IS NULL) ) ) AS tmp1 WHERE maxrecprtval=recprtval ) AS tmp2 ) AS tmp3 WHERE ratusd=maxratusd"
        }

    def getQry_PFM_OD(self, orgarpcod, dstarpcod):
        qrypfm = "SELECT profct FROM CRAPROFCT pfm WHERE orgdstlvl='C' AND cmpcod='" \
                 + self.v_cmpcod + "' AND seqnum=(SELECT MAX(seqnum) FROM CRAPROFCT WHERE ORGCOD=pfm.orgcod AND DSTCOD=pfm.dstcod AND ORGDSTLVL=pfm.orgdstlvl AND CMPCOD=pfm.cmpcod) AND pfm.orgcod=COALESCE((SELECT ctycod FROM shrarpmst WHERE arpcod='" \
                 + orgarpcod + "' and cmpcod='" \
                 + self.v_cmpcod + "'),'" \
                 + orgarpcod + "') AND pfm.dstcod=COALESCE((SELECT ctycod  FROM shrarpmst WHERE arpcod='" \
                 + dstarpcod + "' and cmpcod='" \
                 + self.v_cmpcod + "'),'" \
                 + dstarpcod + "')"
        return qrypfm

    def getQry_PFM(self):
        qrypfm = "SELECT orgcod,dstcod,orgdstlvl,profct FROM CRAPROFCT pfm WHERE orgdstlvl='C' AND cmpcod='" \
                 + self.v_cmpcod + "' AND seqnum=(SELECT MAX(seqnum) FROM CRAPROFCT WHERE ORGCOD=pfm.orgcod AND DSTCOD=pfm.dstcod AND ORGDSTLVL=pfm.orgdstlvl AND CMPCOD=pfm.cmpcod)"
        return qrypfm

    def getQry_Cgocap(self, fltnum, fltdat, density_factor):
        qrycgocap = ""
        if fltnum and fltdat:
            qrycgocap = "select least(totcarcap,(totcarcapvol*" \
                        + density_factor + ")) cgocapwgt  FROM shracrtypmst where cmpcod='" \
                        + self.v_cmpcod + "' AND acrtypcod=(select min(acrtyp) from crmfltoprleg where cmpcod=" \
                        + self.v_cmpcod + " AND fltnum='" \
                        + fltnum + "' AND fltdat=to_date('" \
                        + fltdat + "','YYYY-MM-DD')) AND acrver=1"
        return qrycgocap

    def getQry_ins_AltGblcus(self, frmdat, toodat):
        frmdat = frmdat.strftime('%Y-%m-%d')
        toodat = toodat.strftime('%Y-%m-%d')
        qry_ins_gblcus = "SELECT CMPCOD, FLTNUM, FLTDAT, orgcod SEGORG, dstcod SEGDST, COALESCE(traorg,'-') AWBORG, COALESCE(tradst,'-') AWBDST, traorgcty AWBORGCTY, tradstcty AWBDSTCTY, traorgcnt AWBORGCNT, tradstcnt AWBDSTCNT, traorgreg AWBORGREG, tradstreg AWBDSTREG, ALTIDR, '-' PRDNAM, GBLCUSCOD, CATCOD, STNCOD, NULL DOW, NULL CHGWGTFRM, NULL CHGWGTTOO, 'ALTGBLCUS' RECTYP, 0 RATUSD, 0 OTHCHG, -1 FLTDMD, -1 SEGPROFCT, -1 PRDPROFCT, -1 SEGALTWGT, -1 SEGPRDCLSWGT, -1 CLSWGTPER, CASE WHEN altwgt>0 THEN altwgt ELSE 0 END CLSWGT, 0 REVUSD, 1 CONTRBRTO, (SELECT CASE WHEN flttyp='CO' THEN 'P' WHEN flttyp ='C' THEN 'F' ELSE flttyp END flttyp FROM crmfltoprmst WHERE cmpcod=alt.cmpcod AND fltnum =alt.fltnum AND fltdat =alt.fltdat ) FLTTYP, (SELECT fltrou FROM crmfltoprmst WHERE cmpcod=alt.cmpcod AND fltnum =alt.fltnum AND fltdat =alt.fltdat ) FLTROU, (SELECT fltorg FROM crmfltoprmst WHERE cmpcod=alt.cmpcod AND fltnum =alt.fltnum AND fltdat =alt.fltdat ) FLTORG, (SELECT fltdst FROM crmfltoprmst WHERE cmpcod=alt.cmpcod AND fltnum =alt.fltnum AND fltdat =alt.fltdat )FLTDST, NULL EXEDAT, 0 TAGIDX, 'I' CRMFLG, CURRENT_TIMESTAMP CRMLSTUPDTIM, clsgrswgt, clsvolwgt FROM (SELECT cmpcod, fltnum, fltdat, orgcod, dstcod, traorg, tradst, fltaltidr altidr, gblcuscod, catcod, stncod, alttyp, COALESCE( CASE WHEN wgt < vol*COALESCE( (SELECT cast(parval AS DOUBLE precision) FROM shrsyspar syspar WHERE syspar.parcod = 'capacity.allotment.weightFor1CBMVolume' AND syspar.cmpcod =fltalt.cmpcod ),0) THEN vol *COALESCE( (SELECT cast(parval AS DOUBLE precision) FROM shrsyspar syspar WHERE syspar.parcod = 'capacity.allotment.weightFor1CBMVolume' AND syspar.cmpcod =fltalt.cmpcod ),0) ELSE wgt END ,0) altwgt, COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.traorg ),fltalt.traorg) TRAORGCTY, COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.tradst ),fltalt.tradst) TRADSTCTY, (SELECT cntcod FROM shrctymst WHERE ctycod= COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.traorg ),fltalt.traorg) ) TRAORGCNT, (SELECT cntcod FROM shrctymst WHERE ctycod= COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.tradst ),fltalt.tradst) ) TRADSTCNT, (SELECT regcod FROM shrcntmst WHERE cntcod= (SELECT cntcod FROM shrctymst WHERE ctycod= COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.traorg ),fltalt.tradst) ) ) TRAORGREG, (SELECT regcod FROM shrcntmst WHERE cntcod= (SELECT cntcod FROM shrctymst WHERE ctycod= COALESCE( (SELECT ctycod FROM shrarpmst WHERE arpcod=fltalt.tradst ),fltalt.tradst) ) ) TRADSTREG, COALESCE( wgt,0) clsgrswgt , COALESCE(vol* (SELECT cast(parval AS DOUBLE precision) FROM shrsyspar syspar WHERE syspar.parcod = 'capacity.allotment.weightFor1CBMVolume' AND syspar.cmpcod =fltalt.cmpcod ),0) clsvolwgt FROM crmcapfltalt fltalt WHERE alttyp='G' AND altsta <>'D' AND fltdat BETWEEN to_date('" \
                         + frmdat + "','YYYY-MM-DD') AND to_date('" \
                         + toodat + "','YYYY-MM-DD') AND fltcaridr=" \
                         + self.v_fltcaridr + " AND cmpcod ='" \
                         + self.v_cmpcod + "' AND fltseqnum= (SELECT MAX(fltseqnum) FROM crmcapfltalt WHERE CMPCOD =fltalt.CMPCOD AND FLTCARIDR=fltalt.FLTCARIDR AND FLTNUM =fltalt.FLTNUM AND FLTDAT =fltalt.FLTDAT AND FLTALTIDR=fltalt.FLTALTIDR AND STNCOD =fltalt.STNCOD ) AND NOT EXISTS (SELECT 1 FROM crmfltoprmst WHERE domfltflg='Y' AND cmpcod =fltalt.cmpcod AND fltnum =fltalt.fltnum AND fltdat =fltalt.fltdat ) ) alt"
        return qry_ins_gblcus
